para.programlisting:
#!/bin/sh # # local_fs - check and mount local filesystems # PATH=/sbin:/bin ; export PATH
case $1 in
start) echo "Checking local filesystem integrity." fsck -ATCp if [ $(($?)) -gt $((1)) ]; then echo "Filesystem errors still exist! Manual intervention required." /bin/sh else echo "Remounting / as read-write." mount -o remount,rw / echo "Mounting local filesystems." mount -a fi ;;
stop) echo "Dismounting local filesystems." umount -a echo "Remounting / as read-only." mount -o remount,rw / echo "Flushing disk cache." sync ;;
default) echo "usage: $0 start|stop"; ;;
esac # # end of local_fs
