real_display=`echo $DISPLAY | sed "s/://" | sed "s/\..*//"` case "$real_display" in 0) artsd -F 10 -S 4096 -D /dev/dsp -s 5 -m artsmessage -l 3 -f & ;; 1) artsd -F 10 -S 4096 -D /dev/dsp1 -s 5 -m artsmessage -l 3 -f & ;; 2) artsd -F 10 -S 4096 -a alsa -D hw:2,0 -s 5 -m artsmessage -l 3 -f & ;; esac
